import React, { useEffect, useRef, useState } from 'react';

const CodeGeneratedCursor = () => {
  const cursorRef = useRef(null);
  const [isHovering, setIsHovering] = useState(false);

  useEffect(() => {
    const moveCursor = (e) => {
      if (cursorRef.current) {
        cursorRef.current.style.left = `${e.clientX}px`;
        cursorRef.current.style.top = `${e.clientY}px`;
      }
    };

    const handleMouseEnter = () => setIsHovering(true);
    const handleMouseLeave = () => setIsHovering(false);

    document.addEventListener('mousemove', moveCursor, { passive: true });

    document.querySelectorAll('[data-skill]').forEach(el => {
      el.addEventListener('mouseenter', handleMouseEnter);
      el.addEventListener('mouseleave', handleMouseLeave);
    });

    return () => {
      document.removeEventListener('mousemove', moveCursor);
      document.querySelectorAll('[data-skill]').forEach(el => {
        el.removeEventListener('mouseenter', handleMouseEnter);
        el.removeEventListener('mouseleave', handleMouseLeave);
      });
    };
  }, []);

  return (
    <div
      ref={cursorRef}
      className={`fixed w-6 h-6 pointer-events-none transform -translate-x-1/2 -translate-y-1/2 z-50 transition-all duration-100 ${isHovering ? 'scale-125' : 'scale-100'}`}
      style={{
        width: '0',
        height: '0',
        borderLeft: '8px solid transparent',
        borderRight: '8px solid transparent',
        borderTop: '16px solid white',
        borderRadius: '0.1rem',
        backgroundColor: 'transparent', // Asegurarse de que no haya fondo que tape el triángulo
        boxShadow: isHovering ? '0 0 5px white, 0 0 15px rgba(0, 191, 255, 0.8)' : '0 0 2px rgba(255, 255, 255, 0.5)',
      }}
    />
  );
};

export default CodeGeneratedCursor;
