import React, { useEffect, useRef, useState } from 'react';

const FuturisticCursor = () => {
  const cursorRef = useRef(null);
  const [isHovering, setIsHovering] = useState(false);

  useEffect(() => {
    const moveCursor = (e) => {
      if (cursorRef.current) {
        cursorRef.current.style.left = `${e.clientX}px`;
        cursorRef.current.style.top = `${e.clientY}px`;
      }
    };

    const handleMouseEnter = () => setIsHovering(true);
    const handleMouseLeave = () => setIsHovering(false);

    document.addEventListener('mousemove', moveCursor, { passive: true });

    document.querySelectorAll('[data-skill]').forEach(el => {
      el.addEventListener('mouseenter', handleMouseEnter);
      el.addEventListener('mouseleave', handleMouseLeave);
    });

    return () => {
      document.removeEventListener('mousemove', moveCursor);
      document.querySelectorAll('[data-skill]').forEach(el => {
        el.removeEventListener('mouseenter', handleMouseEnter);
        el.removeEventListener('mouseleave', handleMouseLeave);
      });
    };
  }, []);

  return (
    <div
      ref={cursorRef}
      className={`fixed w-8 h-8 pointer-events-none transform -translate-x-1/2 -translate-y-1/2 z-50 transition-all duration-100 ${isHovering ? 'scale-125' : 'scale-100'}`}
      style={{
        backgroundImage: 'url(http://googleusercontent.com/image_generation_content/1)', // Nueva URL de la imagen generada
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        borderRadius: '0.5rem', // Añadí un pequeño borde redondeado para suavizar un poco los bordes angulares
        boxShadow: isHovering ? '0 0 0 2px white, 0 0 20px 5px rgba(0, 191, 255, 0.5)' : '0 0 0 1px rgba(255,255,255,0.5)', // Cambié el color del brillo a celeste
      }}
    />
  );
};

export default FuturisticCursor;
