import React, { useEffect, useState, useRef } from 'react';

const OptimizedCustomCursor = () => {
  const cursorRef = useRef(null);
  const [isHovering, setIsHovering] = useState(false);

  useEffect(() => {
    const moveCursor = (e) => {
      if (cursorRef.current) {
        cursorRef.current.style.left = `${e.clientX}px`;
        cursorRef.current.style.top = `${e.clientY}px`;
      }
    };

    const handleMouseEnter = () => setIsHovering(true);
    const handleMouseLeave = () => setIsHovering(false);

    document.addEventListener('mousemove', moveCursor, { passive: true }); // Usa passive listener

    document.querySelectorAll('[data-skill]').forEach(el => {
      el.addEventListener('mouseenter', handleMouseEnter);
      el.addEventListener('mouseleave', handleMouseLeave);
    });

    return () => {
      document.removeEventListener('mousemove', moveCursor);
      document.querySelectorAll('[data-skill]').forEach(el => {
        el.removeEventListener('mouseenter', handleMouseEnter);
        el.removeEventListener('mouseleave', handleMouseLeave);
      });
    };
  }, []);

  return (
    <div
      ref={cursorRef}
      className={`fixed w-8 h-8 rounded-full pointer-events-none transform -translate-x-1/2 -translate-y-1/2 z-50 transition-all duration-100 ${isHovering ? 'scale-150 bg-purple-500 mix-blend-difference' : 'scale-100 bg-white'}`}
      style={{
        boxShadow: isHovering ? '0 0 0 2px white, 0 0 20px 5px rgba(156, 39, 176, 0.5)' : '0 0 0 1px rgba(255,255,255,0.5)'
      }}
    />
  );
};

export default OptimizedCustomCursor;
