import React, { useEffect, useRef, useState } from 'react';
import { throttle } from 'lodash';
import './CodingCursor.css'; // Asegúrate de crear este archivo

const CodingCursor = () => {
  const cursorRef = useRef(null);
  const [isHovering, setIsHovering] = useState(false);

  useEffect(() => {
    const moveCursor = throttle((e) => {
      if (cursorRef.current) {
        cursorRef.current.style.transform = `translate(${e.clientX}px, ${e.clientY}px)`;
      }
    }, 80); // Aumenté el intervalo a 80ms para reducir la frecuencia de actualización

    const handleMouseEnter = () => setIsHovering(true);
    const handleMouseLeave = () => setIsHovering(false);

    document.addEventListener('mousemove', moveCursor, { passive: true });

    document.querySelectorAll('[data-skill]').forEach(el => {
      el.addEventListener('mouseenter', handleMouseEnter);
      el.addEventListener('mouseleave', handleMouseLeave);
    });

    return () => {
      document.removeEventListener('mousemove', moveCursor);
      document.querySelectorAll('[data-skill]').forEach(el => {
        el.removeEventListener('mouseenter', handleMouseEnter);
        el.removeEventListener('mouseleave', handleMouseLeave);
      });
      moveCursor.cancel();
    };
  }, []);

  return (
    <div
      ref={cursorRef}
      className={`coding-cursor fixed w-6 h-6 rounded-full pointer-events-none transform -translate-x-1/2 -translate-y-1/2 z-50 transition-all duration-100 ${isHovering ? 'hovering' : ''}`}
    />
  );
};

export default CodingCursor;
